import React, {useState} from 'react';
import {Form, Input, Message, Button} from 'semantic-ui-react';
import {campaignCreate} from '../ethereum/campaign';
import web3 from '../ethereum/web3';
import Router from 'next/router';

export const ContributeForm = ({address}) => {

    const [formState, formChangeState] = useState({
        value: '',
        loading: false,
        errorMessage: ''
    });

    const onContribute = async (e) => {
        e.preventDefault();

        const campaign = campaignCreate(address);

        formChangeState({
            ...formState,
            loading: true,
            errorMessage: ''
        });

        try {
            const accounts = await web3.eth.getAccounts();

            await campaign.methods.contribute().send({
                from: accounts[0],
                value: web3.utils.toWei(formState.value, 'ether')
            })
            Router.replace(`/campaigns/${address}`)
        } catch(e) {
            formChangeState({
                formState,
                errorMessage: e.message
            })
        }

        formChangeState({
            ...formState,
            loading: false
        })
    };

    return(
        <Form onSubmit={(e) => onContribute(e)} error={!!formState.errorMessage}>
            <Form.Field>
                <label>Amount to Contribute</label>
                <Input 
                    value={formState.value || ''}
                    onChange={e => formChangeState({
                        ...formState,
                        value: e.target.value
                    })}
                    label="ether"
                    labelPosition="right"
                />
            </Form.Field>
            <Message error header="Ooops!" content={formState.errorMessage} />
            <Button 
                primary
                disabled={formState.loading}
                loading={formState.loading}
            >
                Contribute!
            </Button>
        </Form>
    )
};
