import Web3 from 'web3';

let web3;

if(typeof window !== 'undefined' && typeof window.ethereum !== 'undefined') {
    // we are in browser and user have metamask
    web3 = new Web3(window.ethereum);
    window.web3 = web3;
} else {

}

export default web3;
