import React, {PureComponent} from 'react';
import dynamic from 'next/dynamic';

export default class CampaignIndex extends PureComponent {
    async componentDidMount() {
        const {factory} = dynamic(import ('../ethereum/factory'), {ssr: false});
        const campaigns = await factory.methods.getDeployedCampaigns().call();
    }

    render() {
        return (

            <div>
                Campaigns index!
                {console.log(factory)}
            </div>
        )
    }
}
