import React, {Component} from 'react';
import {campaignCreate} from '../../ethereum/campaign';
import {Card} from 'semantic-ui-react';
import {withRouter} from 'next/router';
import {Layout} from '../../components/Layout';

export async function getServerSideProps(content) {
    const {campaign_id} = content.query;
    const campaign = campaignCreate(campaign_id); // address

    const summary = await campaign.methods.getSummary().call();

    return {props: {
        minimumContribution: summary[0],
        balance: summary[1],
        requestsCount: summary[2],
        approversCount: summary[3],
        manager: summary[4]
    }}
}

class CampaignShow extends Component {

    renderCards() {
        const {
            minimumContribution, 
            balance,
            requestsCount,
            approversCount,
            manager
        } = this.props;

        const items = [
            {
                header: manager,
                meta: 'Address of manager',
                description: 'The manager created this campaign and can create requests to withdraw money'
            }
        ]

        return <Card.Group items={items} />
    }

    render(){
        return(
            <Layout>
                <h2 className="ui header">Existing campaign</h2>
                {this.renderCards()}
            </Layout>   
        )
    }
};

export default withRouter(CampaignShow);
